UPDATE script_texts SET content_loc4='10秒钟后爆炸！退后！' WHERE entry=-1090018; /* content: 10 seconds to blast! Stand back!!! */
UPDATE script_texts SET content_loc4='5秒钟后爆炸！！！！' WHERE entry=-1090019; /* content: 5 seconds until detonation!!!!! */
