UPDATE script_texts SET content_loc4='醒来吧，我的孩子，来帮助你们的主人！' WHERE entry=-1556018; /* content: Awaken, my children and assist your master! */
UPDATE script_texts SET content_loc4='你的魔法将导致你的毁灭……呱——啊——呱……' WHERE entry=-1556019; /* content: Your magics shall be your undoing... ak-a-ak... */
UPDATE script_texts SET content_loc4='%s回到了石化状态。' WHERE entry=-1556020; /* content: %s returns to stone. */
UPDATE script_texts SET content_loc4='你的力量……呱——呱……将转而对付你……' WHERE entry=-1556021; /* content: Your powers... ak-ak... turn against you... */
