UPDATE script_texts SET content_loc4='我根本没什么感觉。' WHERE entry=-1564019; /* content: I\'m not impressed. */
UPDATE script_texts SET content_loc4='你们觉得很有趣吗？' WHERE entry=-1564020; /* content: Enjoying yourselves? */
