UPDATE script_texts SET content_loc1='오호, 난 새로운 기술을 익혔지... 내 형제, 곰처럼...' WHERE entry=-1568058; /* content: Got me some new tricks... like me brudda bear.... */
UPDATE script_texts SET content_loc1='독수리의 눈을 피할 수는 없다!' WHERE entry=-1568059; /* content: Dere be no hidin\' from da eagle! */
UPDATE script_texts SET content_loc1='오, 내 새로운 형제, 송곳니와 발톱을 보아라!' WHERE entry=-1568060; /* content: Let me introduce you to me new bruddas: fang and claw! */
UPDATE script_texts SET content_loc1='용매를 하늘에서만 찾을 필요는 없다!' WHERE entry=-1568061; /* content: Ya don\' have to look to da sky to see da dragonhawk! */
UPDATE script_texts SET content_loc1='불길로 너희를 끝장내 주마!' WHERE entry=-1568062; /* content: Fire kill you just as quick! */
UPDATE script_texts SET content_loc1='혼들이 오늘 잔치를 벌이겠구나! 의식을 시작해라. 포로를 제물로 바쳐라... 새 손님을 맞아야지!' WHERE entry=-1568068; /* content: Da spirits gonna feast today! Begin da ceremonies, sacrifice da prisoners... make room for our new guests! */
UPDATE script_texts SET content_loc1='아무나 골라 봐라, 침입자들아! 내 사제 모두가 너희를 반겨줄 테니까.' WHERE entry=-1568069; /* content: Take your pick, trespassers! Any of ma priests be happy to accommodate ya. */
UPDATE script_texts SET content_loc1='부끄러울 거 없어. 이전에도 수천 명이 왔었다. 너희만 제물이 되는 게 아니야.' WHERE entry=-1568070; /* content: Don\'t be shy. Thousands have come before you. Ya not be alone in your service. */
UPDATE script_texts SET content_loc1='너희는 실패한다, 이방인들이여. 먼저 온 손님들도 우리 힘만 키워 주고 말았지!' WHERE entry=-1568071; /* content: Ya gonna fail, strangers. Many try before you, but dey only make us stronger! */
UPDATE script_texts SET content_loc1='들인 수고가 아깝군, 침입자들아. 의식이 거의 끝나 간다.' WHERE entry=-1568072; /* content: Your efforts was in vain, trespassers. The rituals nearly be complete. */
UPDATE script_texts SET content_loc1='이제 우리가 텅 비고 의식이 끝나면, 너희가 그 자리를 채우게 될 것이다.' WHERE entry=-1568073; /* content: Soon da cages gonna be empty, da sacrifices be complete, and you gonna take dere places. */
UPDATE script_texts SET content_loc1='시간이 다 됐다. 너희들이 구하지 못한 저 영혼들과 곧 만나게 해 주마.' WHERE entry=-1568074; /* content: Time be running low, strangers. Soon you gonna join da souls of dem ya failed to save. */
UPDATE script_texts SET content_loc1='서둘러라, 사제들! 놈들이 의식을 방해하게 두지 마라!' WHERE entry=-1568075; /* content: Make haste, ma priests! Da rituals must not be interrupted! */
UPDATE script_texts SET content_loc1='시도는 좋았다만, 이제 너희도 실패자의 반열에 들겠구나.' WHERE entry=-1568076; /* content: Ya make a good try... but now you gonna join da ones who already fall. */
UPDATE script_texts SET content_loc1='지켜봐라. 제물을 바치면 혼과의 결속이 강해지지. 이제, 누구도 우릴 막지 못하리라!' WHERE entry=-1568078; /* content: Watch now. Every offering gonna strengthen our ties to da spirit world. Soon, we gonna be unstoppable! */
UPDATE script_texts SET content_loc1='내 계산에 따르면, 충분한 인원이 한꺼번에 징을 울리면 이 문을 막은 봉인이 깨져 안으로 들어갈 수 있을 거요.' WHERE entry=-1568080; /* content: According to my calculations, if enough of us bang the gong at once the seal on these doors will break and we can enter. */
UPDATE script_texts SET content_loc1='%s|1이;가; 곰의 혼이 지닌 정수를 흡수합니다!' WHERE entry=-1568082; /* content: %s absorbs the essence of the bear spirit! */
UPDATE script_texts SET content_loc1='%s|1이;가; 독수리의 혼이 지닌 정수를 흡수합니다!' WHERE entry=-1568083; /* content: %s absorbs the essence of the eagle spirit! */
UPDATE script_texts SET content_loc1='%s|1이;가; 스라소니의 혼이 지닌 정수를 흡수합니다!' WHERE entry=-1568084; /* content: %s absorbs the essence of the lynx spirit! */
UPDATE script_texts SET content_loc1='%s|1이;가; 용매의 혼이 지닌 정수를 흡수합니다!' WHERE entry=-1568085; /* content: %s absorbs the essence of the dragonhawk spirit! */
UPDATE script_texts SET content_loc1='모두 쓸어 주마.' WHERE entry=-1580001; /* content: I will purge you! */
UPDATE script_texts SET content_loc1='진정한 고통은 이제 시작이다!' WHERE entry=-1580002; /* content: Your pain has only begun! */
UPDATE script_texts SET content_loc1='킬제덴의 이름으로!' WHERE entry=-1580003; /* content: In the name of Kil\'jaeden! */
UPDATE script_texts SET content_loc1='난 이미 경고했다!' WHERE entry=-1580004; /* content: You were warned! */
UPDATE script_texts SET content_loc1='나는 완전히 깨어났다! 모두 없애 주마!' WHERE entry=-1580005; /* content: My awakening is complete! You shall all perish! */
UPDATE script_texts SET content_loc1='어서요! 날 잃어 가고 있어!' WHERE entry=-1580008; /* content: Hurry! There is not much of me left! */
UPDATE script_texts SET content_loc1='크나큰 빚을 졌습니다. 킬제덴을 쓰러뜨린다면, 이 세계 역시 그대들에게 빚을 지는 겁니다.' WHERE entry=-1580009; /* content: I am forever in your debt. Once we have triumphed over Kil\'jaeden, this entire world will be in your debt as well. */
UPDATE script_texts SET content_loc1='네 고통은 내 행복이지!' WHERE entry=-1580012; /* content: Your misery is my delight! */
UPDATE script_texts SET content_loc1='피를 보고야 말겠다.' WHERE entry=-1580013; /* content: I will watch you bleed! */
UPDATE script_texts SET content_loc1='말하지 않았나? 나는 항상 이긴다!' WHERE entry=-1580015; /* content: Haven\'t you heard? I always win! */
UPDATE script_texts SET content_loc1='장난은 이걸로 끝이다!' WHERE entry=-1580016; /* content: I have toyed with you long enough! */
UPDATE script_texts SET content_loc1='불쌍한 도마뱀 같으니! 널 기다리는 건 죽음뿐이다!' WHERE entry=-1580017; /* content: Puny lizard! Death is the only answer you\'ll find here! */
UPDATE script_texts SET content_loc1='이대로 뭉개 주마!' WHERE entry=-1580019; /* content: I will crush you! */
UPDATE script_texts SET content_loc1='꽤 괜찮았다만, 이 정도론 아직 멀었다!' WHERE entry=-1580020; /* content: That was fun, but I still await a true challenge! */
UPDATE script_texts SET content_loc1='버러지 같은 놈들!' WHERE entry=-1580023; /* content: Perish, insect! */
UPDATE script_texts SET content_loc1='넌 내 밥이다!' WHERE entry=-1580024; /* content: You are meat! */
UPDATE script_texts SET content_loc1='별거 아니군!' WHERE entry=-1580025; /* content: Too easy! */
UPDATE script_texts SET content_loc1='자, 어서 덤벼 봐라!' WHERE entry=-1580026; /* content: Bring the fight to me! */
UPDATE script_texts SET content_loc1='으하하! 영광스러운 전투를 위하여!' WHERE entry=-1580027; /* content: Another day, another glorious battle! */
UPDATE script_texts SET content_loc1='난 싸우기 위해 산다!' WHERE entry=-1580028; /* content: I live for this! */
UPDATE script_texts SET content_loc1='멈추세요, 영웅들이여! 이 악마를 처치하기 전에 알아내야 할 게 있습니다!' WHERE entry=-1580031; /* content: Hold, friends! There is information to be had before this devil meets his fate! */
UPDATE script_texts SET content_loc1='악마여, 안비나는 어디 있나? 칼렉은 어떻게 된 거지?' WHERE entry=-1580032; /* content: Where is Anveena, demon? What has become of Kalec? */
UPDATE script_texts SET content_loc1='그들이 어디 있는지 말해라!' WHERE entry=-1580033; /* content: You will tell me where they are! */
UPDATE script_texts SET content_loc1='킬제덴 님께 영광을! 맞서는 자에게 죽음을!' WHERE entry=-1580036; /* content: Glory to Kil\'jaeden! Death to all who oppose! */
UPDATE script_texts SET content_loc1='주인님을 위하여!' WHERE entry=-1580037; /* content: I kill for the master! */
UPDATE script_texts SET content_loc1='혼돈...' WHERE entry=-1580049; /* content: Chaos... */
UPDATE script_texts SET content_loc1='어둠이 불꽃을 지피리라!' WHERE entry=-1580052; /* content: Shadow to the aid of fire! */
UPDATE script_texts SET content_loc1='알리테스! 네 불꽃은 내 안에 살아 있어!' WHERE entry=-1580053; /* content: Alythess! Your fire burns within me! */
UPDATE script_texts SET content_loc1='이낙 크레율!' WHERE entry=-1580055; /* content: Ee-nok Kryul! */
UPDATE script_texts SET content_loc1='내가... 희미해진다.' WHERE entry=-1580056; /* content: I... fade. */
UPDATE script_texts SET content_loc1='너희에게 시간이란 사치품이다!' WHERE entry=-1580057; /* content: Time is a luxury you no longer possess! */
UPDATE script_texts SET content_loc1='불꽃이 어둠을 돋우리라!' WHERE entry=-1580058; /* content: Fire to the aid of shadow! */
UPDATE script_texts SET content_loc1='사크로래쉬!' WHERE entry=-1580059; /* content: Sacrolash! */
UPDATE script_texts SET content_loc1='네 운은 이제 다했다!' WHERE entry=-1580063; /* content: Your luck has run its course! */
UPDATE script_texts SET content_loc1='이 순간을 위해 모든 계획을 세웠다!' WHERE entry=-1580064; /* content: All my plans have led to this! */
UPDATE script_texts SET content_loc1='나는 충분히 기다렸다!' WHERE entry=-1580066; /* content: I have waited long enough! */
UPDATE script_texts SET content_loc1='나를 실망시키면 영원히 고통받으리라!' WHERE entry=-1580067; /* content: Fail me and suffer for eternity! */
UPDATE script_texts SET content_loc1='저 소녀를 빨아들여라! 빈 껍데기만 남을 때까지 힘을 모조리 빼앗아!' WHERE entry=-1580068; /* content: Drain the girl! Drain her power until there is nothing but a vacant shell! */
UPDATE script_texts SET content_loc1='혼돈!' WHERE entry=-1580074; /* content: Chaos! */
UPDATE script_texts SET content_loc1='파괴!' WHERE entry=-1580075; /* content: Destruction! */
UPDATE script_texts SET content_loc1='망각!' WHERE entry=-1580076; /* content: Oblivion! */
UPDATE script_texts SET content_loc1='나를 부정할 수는 없다! 이 세계는 멸망하리라!' WHERE entry=-1580077; /* content: I will not be denied! This world shall fall! */
UPDATE script_texts SET content_loc1='으아! 태양샘의 마력이... 나를... 거부한다! 무슨 짓을 한 거지? 무슨 짓을 한 거냐???' WHERE entry=-1580079; /* content: Aggghh! The powers of the Sunwell... turn... against me! What have you done? What have you done??? */
UPDATE script_texts SET content_loc1='난 이제 주인님만을 섬겨요.' WHERE entry=-1580082; /* content: I serve only the Master now. */
UPDATE script_texts SET content_loc1='하지만 난... 잊어버렸어요. 어떻게 돌아가야 하는지 모르겠어요.' WHERE entry=-1580084; /* content: But I\'m... lost. I cannot find my way back. */
UPDATE script_texts SET content_loc1='칼렉... 당신이에요?' WHERE entry=-1580086; /* content: Kalec... Kalec? */
UPDATE script_texts SET content_loc1='다른 수정구에 힘을 불어넣었습니다! 어서요!' WHERE entry=-1580092; /* content: I have empowered another orb! Use it quickly! */
UPDATE script_texts SET content_loc1='다른 수정구가 준비됐습니다! 서두르세요!' WHERE entry=-1580093; /* content: Another orb is ready! Make haste! */
UPDATE script_texts SET content_loc1='모든 힘을 수정구에 실었습니다! 이제 그대들의 몫입니다!' WHERE entry=-1580094; /* content: I have channeled all I can! The power is in your hands! */
UPDATE script_texts SET content_loc1='필멸의 영웅들이여, 오늘 이 승리는 오래전에 예견되었다네. 내 형제가 고통스럽게 내뱉은 패배의 울부짖음은 온 우주에 울려 퍼질 걸세. 그리고 불타는 성전에 맞서 싸우는 이들에게 새로운 희망을 전파하겠지.' WHERE entry=-1580095; /* content: Mortal heroes - your victory here today was foretold long ago. My brother\'s anguished cry of defeat will echo across the universe - bringing renewed hope to all those who still stand against the Burning Crusade. */
UPDATE script_texts SET content_loc1='그대들이 쓰러뜨려야만 했던 엔트로피우스는 한때는 고귀한 나루, 므우루였다네. 생전에 므우루는 막대한 빛과 희망을 뿜는 존재였지. 한동안, 눈먼 이들이 그 힘을 훔치려고 했다네...' WHERE entry=-1580098; /* content: The creature Entropius, whom you were forced to destroy, was once the noble naaru, M\'uru. In life, M\'uru channeled vast energies of LIGHT and HOPE. For a time, a misguided few sought to steal those energies... */
UPDATE script_texts SET content_loc1='저희의 불손함은 용서받을 길이 없습니다. 가장 고귀한 존재 중 하나를 더럽혔으니까요. 이 죄는 씻을 수가 없을 겁니다.' WHERE entry=-1580099; /* content: Our arrogance was unpardonable. We damned one of the most noble beings of all. We may never atone for this sin. */
UPDATE script_texts SET content_loc1='축복받은 조상님들이여! 느껴집니다... 크나큰 사랑이... 넘치는 은총이! 도저히... 말로 표현할 수가 없어요...' WHERE entry=-1580104; /* content: Blessed ancestors! I feel it... so much love... so much grace... there are... no words... impossible to describe... */
UPDATE script_texts SET content_loc1='구원일세, 젊은이. 우리 모두를 감싸 안는...' WHERE entry=-1580105; /* content: Salvation, young one. It waits for us all. */
UPDATE script_texts SET content_loc1='%s|1이;가; 숨을 깊게 들이마십니다.' WHERE entry=-1580107; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc1='놀아줄 시간이 없다!' WHERE entry=-1585000; /* content: You only waste my time! */
UPDATE script_texts SET content_loc1='힘을 원해! 끝없는 힘을!' WHERE entry=-1585001; /* content: My hunger knows no bounds! */
UPDATE script_texts SET content_loc1='흥, 기분 전환이 됐군.' WHERE entry=-1585003; /* content: Enough distractions! */
UPDATE script_texts SET content_loc1='나는 무적이다!' WHERE entry=-1585004; /* content: I am invincible! */
UPDATE script_texts SET content_loc1='억제할 수... 없다...' WHERE entry=-1585008; /* content: Un...con...tainable. */
UPDATE script_texts SET content_loc1='삼켰... 다...' WHERE entry=-1585010; /* content: Con...sume. */
UPDATE script_texts SET content_loc1='이젠 화가 나는데.' WHERE entry=-1585015; /* content: Now I\'m getting annoyed. */
UPDATE script_texts SET content_loc1='시작이 좋은걸.' WHERE entry=-1585017; /* content: I call that a good start. */
UPDATE script_texts SET content_loc1='혼자란 외로운 법이지.' WHERE entry=-1585020; /* content: One is such a lonely number. */
UPDATE script_texts SET content_loc1='아, 너무 시시했어.' WHERE entry=-1585021; /* content: It\'s been a kick, really. */
UPDATE script_texts SET content_loc1='복수여, 타올라라!' WHERE entry=-1585024; /* content: Vengeance burns! */
UPDATE script_texts SET content_loc1='어디로 갈까? 무얼 할까? 모두 다 부질없는 것! 고통과 죽음만이 기다릴 뿐...' WHERE entry=-1999905; /* content: Where to go? What to do? So many choices that all end in pain, end in death. */
UPDATE script_texts SET content_loc1='어욱...' WHERE entry=-1999918; /* content: ugh... */
UPDATE script_texts SET content_loc1='안녕하세요!' WHERE entry=-1999925; /* content: Hi! */

