UPDATE db_script_string SET content_loc8='Клянусь звездами! Дух призван!' WHERE entry=2000000060; /* content: By the stars! A spirit has been summoned! */
UPDATE db_script_string SET content_loc8='Это воистину тайна прошлого! Но и ключ к нашему будущему!' WHERE entry=2000000061; /* content: It\'s a mystery of the past indeed! But a key to our future! */
