UPDATE db_script_string SET content_loc8='Следуйте за мной, хороший $C. Я сделаю ваш чепрак быстрее, чем я мог бы выпить залпом Дворфийский портер. Ммммм... портер.' WHERE entry=2000000063; /* content: Follow me, good $C. I\'ll have your barding done faster than I could down a Dwarven stout. Mmmm... stout. */
UPDATE db_script_string SET content_loc8='Хорошо, это должно завершиться. Ну-ка, назад к магазину, закончим наши дела!' WHERE entry=2000000064; /* content: Well, that should do it. Come now, back to the shop to finish our business! */
UPDATE db_script_string SET content_loc8='Мы не потерпим на нашей земле слуг демонов!' WHERE entry=2000000065; /* content: We will suffer no demon\'s servant in our lands! */
