UPDATE db_script_string SET content_loc7='Debería haber tomado la pastilla translúcida.' WHERE entry=2000005228; /* content: I should have taken the translucent pill. */
UPDATE db_script_string SET content_loc7='¡No se me paga lo suficiente por este excremento de vaca!' WHERE entry=2000005229; /* content: I don\'t get paid enough for this bull excrement! */
UPDATE db_script_string SET content_loc7='Cuanto más esperen, menos futuro tendrán para que se los prediga.' WHERE entry=2000005232; /* content: The longer you wait, the less future you have for me to foretell. */
UPDATE db_script_string SET content_loc7='¡No puedo pagar tantos impuestos! ¿Seguridad Social? ¿SS? ¡No le queda mal el nombre!' WHERE entry=2000005234; /* content: I can\'t take these taxes! F.I.C.A. me? F.I.C.A. YOU! */
UPDATE db_script_string SET content_loc7='¡Disfruta de las actuaciones de Perry Gatner en unos instantes!' WHERE entry=2000005302; /* content: Check out the comedy stylings of Perry Gatner in just a few minutes! */
UPDATE db_script_string SET content_loc7='Si estás $glisto:lista; para divertirte, vete a la taberna del Fin del Mundo.' WHERE entry=2000005303; /* content: If you are ready to laugh, head over to the World\'s End Tavern! */
UPDATE db_script_string SET content_loc7='Ahora en la Ciudad de Shattrath: ¡los Tauren Chieftains! ¡Actuarán en la Taberna del Fin del Mundo en solo unos minutos!' WHERE entry=2000005304; /* content: Now, in Shattrath City: The Tauren Chieftains! Playing in the World\'s End Tavern at the top of the hour! */
UPDATE db_script_string SET content_loc7='¡Dijiste que Brogg podía quedarse! ¡Prometiste a Brogg que ayudarías a recuperar el montículo del clan Quebrantarrocas!' WHERE entry=2000005306; /* content: You said Brogg could stay here! You promised Brogg you\'d help regain Stonemaul clan mound! */
UPDATE db_script_string SET content_loc7='Mmmm... ¿Dónde está mi caña de pescar? Oh, ahí está.' WHERE entry=2000005307; /* content: Hmmm... Where is my fishing hook? Oh, there it is. */
UPDATE db_script_string SET content_loc7='Veamos si los peces pican...' WHERE entry=2000005308; /* content: Let\'s see if the fish are biting. */
UPDATE db_script_string SET content_loc7='Mmmm... Creo que necesito preparar cebos. Iré a mirar en el vagón de mi padre.' WHERE entry=2000005309; /* content: Hmmm... I think I need better bait. I\'ll check my father\'s wagon. */
UPDATE db_script_string SET content_loc7='¡Desechos de jabaespín! Eso debería de servir. A los peces les encantan los jabaespines.' WHERE entry=2000005310; /* content: Quillboar scraps! These should do the trick. Fish love Quillboar. */
UPDATE db_script_string SET content_loc7='¡He cogido uno! Voy a ver si alguno de los guardias tiene hambre. ¡Vamos, Colmillo!' WHERE entry=2000005311; /* content: Caught one! I\'ll see if any of the guards are hungry. Come on Fang! */
UPDATE db_script_string SET content_loc7='He pescado un pez... Si tienes hambre' WHERE entry=2000005312; /* content: I caught a fish if you are hungry... */
UPDATE db_script_string SET content_loc7='¡Muy bien hecho! ¡Traes honor a la Horda, chico, te lo agradecemos!' WHERE entry=2000005313; /* content: A nice catch it is at that! You bring honor to the Horde boy, We thank you! */
UPDATE db_script_string SET content_loc7='¿Quiere algo más de comer, señor?' WHERE entry=2000005314; /* content: Would you like something more to eat sir? */
UPDATE db_script_string SET content_loc7='¡Más pan, chico!' WHERE entry=2000005315; /* content: More bread boy! */
UPDATE db_script_string SET content_loc7='Ahora mismo, señor.' WHERE entry=2000005316; /* content: Right away sir. */
UPDATE db_script_string SET content_loc7='Aquí está su pan, señor.' WHERE entry=2000005317; /* content: Here is your bread sir. */
UPDATE db_script_string SET content_loc7='Aaaah... Muy bien. ¡Ahora, fuera!' WHERE entry=2000005318; /* content: Aaahh... Very good. Now scat! */
UPDATE db_script_string SET content_loc7='Como desees, señor.' WHERE entry=2000005319; /* content: As you wish sir. */
UPDATE db_script_string SET content_loc7='¿Puedo ir a buscar más piedras para mi collar, papá?' WHERE entry=2000005320; /* content: Can I go collect more rocks for my necklace, Papa? */
UPDATE db_script_string SET content_loc7='¡Dabu! ¡Tendré cuidado, papá!' WHERE entry=2000005322; /* content: Dabu! I will be careful Papa! */
UPDATE db_script_string SET content_loc7='Ooohh... Esta piedra es bonita y me gusta mucho el color. A ver si puedo encontrar más iguales...' WHERE entry=2000005323; /* content: Ooohh... This rock is nice and colorful. Let\'s see if I can find more like it... */
UPDATE db_script_string SET content_loc7='Mmm... Esa piedra es bonita, pero demasiado pequeña.' WHERE entry=2000005324; /* content: Hmm... That rock is nice, but too small. */
UPDATE db_script_string SET content_loc7='Seguro que a mi amigo Gruk le gustaría esta piedra para la lanza que está haciendo. Es muy afilada... Espero que le guste.' WHERE entry=2000005326; /* content: I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it. */
UPDATE db_script_string SET content_loc7='Ya está... Con esto debería tener suficientes piedras para hacer un collar. De todas formas, ya debería volver a casa...' WHERE entry=2000005327; /* content: There... That should be enough rocks to make a necklace. I should probably get home now anyway. */
UPDATE db_script_string SET content_loc7='Te he traído un regalo de bienvenida, Jarl.' WHERE entry=2000005328; /* content: I brought you a housewarming gift, Jarl. */
UPDATE db_script_string SET content_loc7='Vamos, ábrelo. Creo que te gustará.' WHERE entry=2000005329; /* content: Go on, open it. I think you\'ll like it. */
UPDATE db_script_string SET content_loc7='¿Jarl?' WHERE entry=2000005330; /* content: Jarl? */
UPDATE db_script_string SET content_loc7='A menos que sea algo que se pueda comer, no lo quiero.' WHERE entry=2000005331; /* content: Unless it\'s something I can eat, I don\'t want it. */
UPDATE db_script_string SET content_loc7='Jarl, no has sido tú mismo desde que llegué. ¿Qué está pasando?' WHERE entry=2000005332; /* content: Jarl, you haven\'t been yourself ever since I arrived. What\'s going on? */
UPDATE db_script_string SET content_loc7='Nada. ¿Quieres ayudarme a preparar un estofado de anca de rana?' WHERE entry=2000005333; /* content: Nothing. Wanna help me whip up some frog leg stew? */
UPDATE db_script_string SET content_loc7='¡He venido a visitarte, Jarl! ¡Y te encuentro viviendo en una cabaña vacía en medio de la nada balbuceando sobre ranas! ¡Eres un caso perdido!' WHERE entry=2000005334; /* content: I came out here to visit you, Jarl! And I find you living in an empty cabin in the middle of nowhere, babbling about frogs? You\'re hopeless! */
UPDATE db_script_string SET content_loc7='Y no descansaré hasta llegar al final de este asunto... Así que si necesitas mi ayuda de nuevo, ya sabes dónde encontrarme.' WHERE entry=2000005335; /* content: And I am determined to find out more... So if you need my help again, you know where I\'ll be. */
UPDATE db_script_string SET content_loc7='¿Anaia?... ¿Me engañan mis ojos? ¿Eres tú de verdad?' WHERE entry=2000005336; /* content: Anaya...? Do my eyes deceive me? Is it really you? */
UPDATE db_script_string SET content_loc7='Él ha sido cruel con nosotros, amor mío, pero ten por seguro que ahora sí permaneceremos unidos.' WHERE entry=2000005337; /* content: The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited. */
UPDATE db_script_string SET content_loc7='El destino ha sido muy cruel al permitirnos volver a estar juntos solo tras miles de años de separación...' WHERE entry=2000005338; /* content: That the fates should be so cruel as to permit us only this after a thousand years apart... */
UPDATE db_script_string SET content_loc7='¿Me odias, amor mío? Por haber tenido que destruir tu forma viviente y por liberar tu espíritu de tan triste cautiverio...' WHERE entry=2000005339; /* content: Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage. */
UPDATE db_script_string SET content_loc7='No permitas que esto atormente tu corazón, amor mío. Me has liberado de la esclavitud y por ello te amo más aún.' WHERE entry=2000005340; /* content: Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more. */
UPDATE db_script_string SET content_loc7='Desafortunadamente, esto también debe interrumpirse... Los lazos que me atan a este mundo se debilitan y me arrastran...' WHERE entry=2000005341; /* content: Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away... */
UPDATE db_script_string SET content_loc7='¡No! Anaia... ¡Anaia! ¡No me abandones! Por favor...' WHERE entry=2000005342; /* content: No! Anaya... Anaya! Don\'t leave me! Please... */
UPDATE db_script_string SET content_loc7='Adiós, Cerellean, hasta que volvamos a reunirnos...' WHERE entry=2000005343; /* content: Farewell, Cerellean, until we are joined once again... */
UPDATE db_script_string SET content_loc7='¿Cómo, amor mío? ¿Cómo encontraré fuerzas para enfrentarme a las edades del mundo sin ti a mi lado...?' WHERE entry=2000005345; /* content: How, my love? How will I find the strength to face the ages of the world without you by my side... */
UPDATE db_script_string SET content_loc7='¡Saludos, Gezhe! Se han enviado diecisiete pedidos más al sector Hara\'samid. Los mercados de la veladrita pesada se han venido abajo, tal y como predijiste.' WHERE entry=2000005362; /* content: Hail, Gezhe!  Seventeen more orders have been placed in the Hara\'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted. */
UPDATE db_script_string SET content_loc7='Las exportaciones de Heraazi están subiendo. Ya hemos untado a los oficiales de aduanas que hacía falta y el contrabando empezará a llegar en cuestión de días.' WHERE entry=2000005363; /* content: Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days. */
UPDATE db_script_string SET content_loc7='No me sorprende nada. Vuelve con alguna noticia de verdad, ¿de acuerdo?' WHERE entry=2000005364; /* content: Nothing surprising there.  Come back with some real news, will you? */
UPDATE db_script_string SET content_loc7='Lo que pensaba. Está bien. Puedes irte.' WHERE entry=2000005365; /* content: As expected.  Very well, dismissed. */
UPDATE db_script_string SET content_loc7='Con tu venia, señor.' WHERE entry=2000005366; /* content: By your leave, sir. */
UPDATE db_script_string SET content_loc7='Si estás aquí, significa que ya puedes empezar a estudiar la invocación de cohortes demoníacas para hacer lo que te plazca. Empezaremos por las criaturas más humildes que podrás invocar y continuaremos a partir de ahí. Comencemos.' WHERE entry=2000005367; /* content: If you\'re here, then it means you are prepared to begin the study of summoning demonic cohorts to do your bidding. We will start with the lowliest creatures you will be able to call and continue from there. Let us begin. */
UPDATE db_script_string SET content_loc7='La criatura más fácil de invocar para ti es el diablillo. A estas alturas, ya deberías ser capaz de crear al esbirro, pero por razones de complejidad, empezaré por él.' WHERE entry=2000005368; /* content: The easiest creature for you to summon is the imp. You should already be able to bring forth this minion but for completeness\' sake I will start with him. */
UPDATE db_script_string SET content_loc7='Esta pequeña bestia hedionda es el diablillo. Es pequeño y débil, lo que lo convierte en un ser prácticamente inútil como escudo de carne y hueso y su capacidad de destrucción es realmente mediocre. Esta criatura resulta más útil como apoyo de un grupo más grande.' WHERE entry=2000005369; /* content: This foul little beast is the imp. It is small and weak, making it almost useless as a meatshield, and its damage output is mediocre at best. This creature is best used for support of a larger group. */
UPDATE db_script_string SET content_loc7='Ahora que has podido estudiar el diablillo, pasemos al siguiente esbirro que podrás invocar, el abisario.' WHERE entry=2000005370; /* content: Now that you have had a chance to study the imp, let us move on to the next minion you will be able to summon, the voidwalker. */
UPDATE db_script_string SET content_loc7='Este ente demoníaco se conoce como abisario. Si fuerza y resistencia son importantes, resultando ideal para la defensa. Envíalo al frente para atacar a tu enemigo y luego úsalo como escudo mientras utilizas tus encantamientos y habilidades para extinguir la vida de tu oponente.' WHERE entry=2000005371; /* content: This demonic entity is known as the Voidwalker. Its strength and endurance are significant, making it ideal for defense. Send it to attack your enemy, then use it as a shield while you use your spells and abilities to drain away your opponent\'s life. */
UPDATE db_script_string SET content_loc7='Merece la pena contemplarlo, por si no lo has visto antes. Se trata de una criatura realmente impresionante dentro como fuera del campo de batalla. Ahora, echemos un vistazo a lo que estoy seguro que todos ustedes, alumnos varones, han estado esperando. El súcubo.' WHERE entry=2000005372; /* content: If you\'ve never seen one, it is a sight to behold. A very impressive creature both on and off the field of battle. Next, let us take a look at what I am sure all you male students have been waiting for. The succubus. */
UPDATE db_script_string SET content_loc7='Muy bien. Además de la distracción evidente que un esbirro como este supondrá para sus enemigos masculinos, ella también tiene una capacidad de destrucción impresionante. No obstante, su frágil resistencia la hace casi inservible como escudo.' WHERE entry=2000005373; /* content: All right now. Aside from the obvious distractions a minion like this will provide against your more masculine foes, she is also capable of dealing out impressive amounts of damage. However, her fragile endurance makes her almost useless as a shield. */
UPDATE db_script_string SET content_loc7='Estudia mucho y quizás algún día podrás invocar a tu propio esbirro, pero ahora pasaremos a estudiar al manáfago.' WHERE entry=2000005374; /* content: Study hard and you might one day be able to summon one on your own, but for now it\'s time to move on to the felhunter. */
UPDATE db_script_string SET content_loc7='Lo que tenéis delante es un manáfago. Entre los talentos naturales de esta criatura está la capacidad de neutralizar encantamientos y otras habilidades, convirtiéndola en un ser inigualable a la hora de enfrentarse a un oponente con poderes mágicos.' WHERE entry=2000005375; /* content: What you see before you is a felhunter. This creature\'s natural talents include spell lock and other abilities which make it unequalled when facing a magically attuned opponent. */
UPDATE db_script_string SET content_loc7='A la hora de enfrentarse a cualquier taumaturgo, esta bestia salvaje será tu mejor amigo. Veamos ahora algo ligeramente distinto. La siguiente criatura te ayudará en tus viajes y hará más agradables tus viajes futuros. Vamos a echar un vistazo a un corcel vil.' WHERE entry=2000005376; /* content: When facing a spellcaster of any kind, this feral beast will be your best friend. Now, let us take a look at something a bit different. This next creature will aid your travels and make your future journeys much easier. Let\'s take a look at a felsteed. */
UPDATE db_script_string SET content_loc7='Aquí lo tenéis. Nuestra lección sobre las invocaciones ha llegado a su fin. En breve iniciaremos otra clase, así que si queréis poneros al día, os podéis quedar por aquí.' WHERE entry=2000005378; /* content: There you have it. Our lesson on summoning has come to an end. A new class will begin shortly, so if you wish to brush up, feel free to stay around. */
UPDATE db_script_string SET content_loc7='¡Uf! Aquí está mi sombrero de la suerte. Lo había buscado por todas partes.' WHERE entry=2000005379; /* content: Phew! There\'s my lucky hat. I\'ve been looking for it everywhere. */
UPDATE db_script_string SET content_loc7='No sé en qué estaba pensando. Mira que salir sin mi espada... Me la habría puesto si la hubiera visto aquí...' WHERE entry=2000005380; /* content: I don\'t know what I was thinking, going out without my sword. I would\'ve put it on if I\'d seen it here... */
UPDATE db_script_string SET content_loc7='No puedo seguir sin mi capa. Espero que esté aquí...' WHERE entry=2000005381; /* content: I can\'t possibly go out without my cloak. I hope it\'s in here... */
UPDATE db_script_string SET content_loc7='¡Aquí está! Juraría que no estaba aquí la última vez que miré.' WHERE entry=2000005382; /* content: There it is! I could\'ve sworn it wasn\'t here last time I checked... */
UPDATE db_script_string SET content_loc7='No puedo dormir si no me cuentan un buen cuento. Ahora, seguro que duermo bien.' WHERE entry=2000005383; /* content: I can\'t sleep without a good bedtime story. Now I\'m certain to rest well. */
UPDATE db_script_string SET content_loc7='El sacerdote se arrodilla y reza en silencio.' WHERE entry=2000005409; /* content: The priest kneels in silent prayer. */
UPDATE db_script_string SET content_loc7='Luz, dame fuerza.' WHERE entry=2000005410; /* content: Light, give me strength. */
UPDATE db_script_string SET content_loc7='Luz, guía a los caídos de la falsedad a la verdad, de la oscuridad hacia la luz y de la muerte a la vida infinita.' WHERE entry=2000005411; /* content: Light, lead the fallen from untruth to truth, from darkness to Light and from death to immortality. */
UPDATE db_script_string SET content_loc7='¡¡Nooooo!!' WHERE entry=2000005415; /* content: NOOOOO!!! */
UPDATE db_script_string SET content_loc7='Amigos... Os he fallado...' WHERE entry=2000005416; /* content: Friends... I\'ve failed you... */
UPDATE db_script_string SET content_loc7='No, amigo mío... Nosotros te hemos fallado. Te hemos fallado.' WHERE entry=2000005417; /* content: No my friend... we failed you... we failed you. */
UPDATE db_script_string SET content_loc7='%s coge la bandera naga.' WHERE entry=2000005418; /* content: %s picks up the naga flag. */
UPDATE db_script_string SET content_loc7='¿Cuál es la gran idea, Chispik? ¿Por qué convocas esta reunión?' WHERE entry=2000005419; /* content: What\'s the big idea, Spark? Why\'d you call for this meeting? */
UPDATE db_script_string SET content_loc7='¿Cuál es la gran idea? ¡Casi fastidias mi tapadera, idiota! Te dije que pusieras la brújula y los mapas de navegación en un lugar seguro – no fuera, en campo abierto, para que cualquier estúpido los descubra.' WHERE entry=2000005420; /* content: What\'s the big idea? You nearly blew my cover, idiot! I told you to put the compass and navigation maps somewhere safe - not out in the open for any fool to discover. */
UPDATE db_script_string SET content_loc7='El maestro ha hecho todo lo posible para obtener información sobre el paradero de El Exodar. Podrías haber fastidiado toda la operación, incluyendo la tapadera de nuestro espía, que ocupa un puesto de confianza.' WHERE entry=2000005421; /* content: The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside. */
UPDATE db_script_string SET content_loc7='¡Relájate, Chispik! Lo tengo todo bajo control. Les quitaremos El Exodar de debajo de sus mismos pies – haciéndonos muy, muy ricos tú y yo en el proceso.' WHERE entry=2000005422; /* content: Relax, Spark! I have it all under control. We\'ll strip mine the Exodar right out from under \'em - making both you and I very, very rich in the process. */
UPDATE db_script_string SET content_loc7='¿Relajarme? ¿Sabes lo que hace Kael\'thas con los que le fallan, Geezle? Sufrimiento y dolor eternos... ¡Así que ten cuidado, estúpido!' WHERE entry=2000005423; /* content: Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool. */
UPDATE db_script_string SET content_loc7='Nuestros exploradores de Bruma de Sangre han encontrado a nuestro contacto. El estúpido, Velen, pronto se dejará abierto e indefenso – ¡el tiempo suficiente para que ataquemos! Ahora sal de mi vista antes de que te evapore...' WHERE entry=2000005424; /* content: Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you... */
UPDATE db_script_string SET content_loc7='Sí, señor. No volverá a pasar...' WHERE entry=2000005425; /* content: Yes, sir. It won\'t happen again... */
UPDATE db_script_string SET content_loc7='¡Te lo agradezco!' WHERE entry=2000005544; /* content: You have my thanks! */
UPDATE db_script_string SET content_loc7='Mi espada Archeus me sirvió bien en vida, pero al abandonar mi espíritu esta infeliz existencia, ya no la necesito más.' WHERE entry=2000005546; /* content: My sword Archeus served me well in life, but as at last my spirit may pass from the unhappy existence, I need it no longer. */
UPDATE db_script_string SET content_loc7='Me aferraré al amor de mi hija y espero encontrar el perdón bajo la Luz por mis pecados.' WHERE entry=2000005547; /* content: I shall cling to the love of my daughter and hope that I will find forgiveness under the Light for my sins. */

SET NAMES 'latin1';
