UPDATE script_texts SET content_loc5='來得快，去得也快。' WHERE entry=-1564025; /* content: Easy come, easy go. */
UPDATE script_texts SET content_loc5='多麼快樂的結局啊。' WHERE entry=-1564026; /* content: So much for a happy ending. */
UPDATE script_texts SET content_loc5='不要浪費我的感情了﹗' WHERE entry=-1564027; /* content: Stop toying with my emotions! */
UPDATE script_texts SET content_loc5='部落會...毀滅你們。' WHERE entry=-1564029; /* content: Horde will... crush you. */
UPDATE script_texts SET content_loc5='更多﹗我還要更多﹗' WHERE entry=-1564031; /* content: More! I want more! */
UPDATE script_texts SET content_loc5='我感到飢餓!' WHERE entry=-1564033; /* content: I hunger! */
UPDATE script_texts SET content_loc5='我要把你們撕成碎片﹗' WHERE entry=-1564035; /* content: I\'ll rip the meat from your bones! */
UPDATE script_texts SET content_loc5='我要報仇!' WHERE entry=-1564038; /* content: Vengeance is mine! */
UPDATE script_texts SET content_loc5='你在害怕什麼?' WHERE entry=-1564041; /* content: What are you afraid of? */
UPDATE script_texts SET content_loc5='我有些東西給你...' WHERE entry=-1564044; /* content: I have something for you... */
UPDATE script_texts SET content_loc5='你要表現出足夠的尊敬﹗' WHERE entry=-1564045; /* content: YOU WILL SHOW THE PROPER RESPECT! */
UPDATE script_texts SET content_loc5='等待你們的只有痛苦與折磨﹗' WHERE entry=-1564047; /* content: Pain and suffering are all that await you! */
UPDATE script_texts SET content_loc5='不要留下我一個人﹗' WHERE entry=-1564048; /* content: Don\'t leave me alone! */
UPDATE script_texts SET content_loc5='我不願意這樣﹗' WHERE entry=-1564050; /* content: I didn\'t ask for this! */
UPDATE script_texts SET content_loc5='我不想回去!' WHERE entry=-1564052; /* content: I don\'t want to go back! */
UPDATE script_texts SET content_loc5='現在，我該做些什麼?' WHERE entry=-1564053; /* content: Now what do I do? */
UPDATE script_texts SET content_loc5='你可以得到任何你想要的東西...只要付得起代價。' WHERE entry=-1564055; /* content: You can have anything you desire... for a price. */
UPDATE script_texts SET content_loc5='你眼高手低。' WHERE entry=-1564058; /* content: Your reach exceeds your grasp. */
UPDATE script_texts SET content_loc5='當心吧，我復活了﹗' WHERE entry=-1564062; /* content: Beware: I live! */
UPDATE script_texts SET content_loc5='太愚蠢了...' WHERE entry=-1564063; /* content: So... foolish. */
UPDATE script_texts SET content_loc5='跪下!' WHERE entry=-1564066; /* content: On your knees! */
UPDATE script_texts SET content_loc5='你們要考驗我嗎?' WHERE entry=-1564069; /* content: You wish to test me? */
UPDATE script_texts SET content_loc5='我還有更重要的事情要做...' WHERE entry=-1564070; /* content: I have better things to do... */
UPDATE script_texts SET content_loc5='不逃走就受死吧!' WHERE entry=-1564071; /* content: Flee or die! */
UPDATE script_texts SET content_loc5='通用語...多麼粗俗的語言。' WHERE entry=-1564072; /* content: Common... such a crude language. Bandal! */
UPDATE script_texts SET content_loc5='遊戲結束了!' WHERE entry=-1564073; /* content: Enough games! */
UPDATE script_texts SET content_loc5='為了奎爾薩拉斯!為了太陽之井!' WHERE entry=-1564075; /* content: For Quel\'Thalas! For the Sunwell! */
UPDATE script_texts SET content_loc5='沒有第二次機會了﹗' WHERE entry=-1564079; /* content: No second chances! */
UPDATE script_texts SET content_loc5='你是我的!' WHERE entry=-1564081; /* content: You are mine! */
UPDATE script_texts SET content_loc5='(薩拉斯語)以太陽之光!' WHERE entry=-1564082; /* content: Anar\'alah belore! */
UPDATE script_texts SET content_loc5='我有很多驚喜給你們﹗' WHERE entry=-1564083; /* content: I\'m full of surprises! */
